import React, { useState, useEffect } from 'react';
import FlipCard from './FlipCard';
import { getTimerState } from '../utils/timerStorage';

function CountdownTimer() {
  const [time, setTime] = useState({ hours: '00', minutes: '00', seconds: '00' });
  const [status, setStatus] = useState({ text: 'Timer Paused', bg: '#fef3c7', color: '#92400e' });

  useEffect(() => {
    const updateTimer = () => {
      const state = getTimerState();
      let timeLeft = state.remainingTime;

      if (state.isRunning && state.endTime) {
        timeLeft = Math.max(0, state.endTime - Date.now());
      }

      const hours = Math.floor(timeLeft / (1000 * 60 * 60));
      const minutes = Math.floor((timeLeft % (1000 * 60 * 60)) / (1000 * 60));
      const seconds = Math.floor((timeLeft % (1000 * 60)) / 1000);

      setTime({
        hours: String(hours).padStart(2, '0'),
        minutes: String(minutes).padStart(2, '0'),
        seconds: String(seconds).padStart(2, '0')
      });

      if (timeLeft <= 0) {
        setStatus({ text: "Time's Up!", bg: '#fee2e2', color: '#991b1b' });
      } else if (state.isRunning) {
        setStatus({ text: 'Timer Running', bg: '#d1fae5', color: '#065f46' });
      } else {
        setStatus({ text: 'Timer Paused', bg: '#fef3c7', color: '#92400e' });
      }
    };

    updateTimer();
    const interval = setInterval(updateTimer, 100);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="container">
      <h1>24 Hour Hackathon</h1>
      <div className="countdown">
        <div className="time-unit">
          <div className="digits">
            <FlipCard value={time.hours[0]} />
            <FlipCard value={time.hours[1]} />
          </div>
          <span className="label">Hours</span>
        </div>
        <div className="time-unit">
          <div className="digits">
            <FlipCard value={time.minutes[0]} />
            <FlipCard value={time.minutes[1]} />
          </div>
          <span className="label">Minutes</span>
        </div>
        <div className="time-unit">
          <div className="digits">
            <FlipCard value={time.seconds[0]} />
            <FlipCard value={time.seconds[1]} />
          </div>
          <span className="label">Seconds</span>
        </div>
      </div>
      <div className="status" style={{ background: status.bg, color: status.color }}>
        {status.text}
      </div>
    </div>
  );
}

export default CountdownTimer;
